import React from "react";
import ReactDOM from "react-dom";
import { useForm, Controller } from "react-hook-form";
import { Input } from "@material-ui/core";
import {useDispatch} from "react-redux";
import {logIn} from "../redux/actions/authAction";
import {useIsAuth} from "../context/AuthContextProvider";
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { addUser } from "../redux/actions/userAction";
import style from "../components/login.module.css"

export const Login = () => {
  const { control, handleSubmit, formState: {errors} } = useForm();
  const [age, setAge] = React.useState('');
  const dispatch =useDispatch()
  const {setIsAuth} = useIsAuth()

  const onSubmit = async(data) => {
    // setIsAuth(true)
    const userObject = {
     ...data, role: age
    }
    console.log(userObject)
    await addUser(data)
  };
  const changeState = (event) => {
    setAge(event.target.value);
  }


  return (
    <div className={style.containerFormStyle}>
      <form className={style.form} onSubmit={handleSubmit(onSubmit)}>
        <label className={style.label}>Login</label>
        <Controller
          render={({ field }) => <Input className={style.input} {...field} />}
          rules={{
            required: "Please, enter your surname.",
          }}
          name="login"
          control={control}
          defaultValue=""
          className="materialUIInput"
        />
        {<p>{errors.login?.message }</p>}
        <label className={style.label}>Password</label>
        <Controller
          render={({ field }) => <Input className={style.input} {...field} />}
          rules={{
            required: "Wow, you still don't have a password?",
          }}
          name="password"
          control={control}
          defaultValue=""
          className="materialUIInput"
        />
        {<p>{errors.password?.message}</p>}
        <FormControl variant="standard" sx={{ m: 1, minWidth: 120 }}>
          <InputLabel id="demo-simple-select-standard-label">
            Choose dataset
          </InputLabel>
          <Select
            labelId="demo-simple-select-standard-label"
            name="selectform"
            value={age}
            onChange={changeState}
            label="Choose dataset"
            required={{
              required: "Please, chose there",
            }}
          >
            <MenuItem value={"Romashka"}>Romashka</MenuItem>
            <MenuItem value={"VTB"}>VTB</MenuItem>
          </Select>
          {<p>{errors.selectform?.message}</p>}
        </FormControl>
        <div>
          <button className={style.button} type="button">
            Отправить
          </button>
        </div>
      </form>
    </div>
  );
};
