import React from "react";
import ReactDOM from "react-dom";
import { useForm, Controller } from "react-hook-form";
import { Input } from "@material-ui/core";
import "../components/styles.css"
import {useDispatch} from "react-redux";
import {logIn} from "../redux/actions/authAction";
import {useIsAuth} from "../context/AuthContextProvider";
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { addUser } from "../redux/actions/userAction";

export const Login = () => {
  const { control, handleSubmit, formState: {errors} } = useForm();
  const [age, setAge] = React.useState('');
  const dispatch =useDispatch()
  const {setIsAuth} = useIsAuth()

  const onSubmit = async(data) => {
    // setIsAuth(true)
    const userObject = {
     ...data, role: age
    }
    console.log(userObject)
    await addUser(data)
  };
  const changeState = (event) => {
    setAge(event.target.value);
  }


  return (
    <div >
      <form onSubmit={handleSubmit(onSubmit)}>
        <label>Login</label>
        <Controller
          render={({ field }) => <Input {...field} />}
          rules={{
            required: "Please, enter your surname."
          }}
          name="login"
          control={control}
          defaultValue=""
          className="materialUIInput"
        />
        {<p>{errors.login?.message}</p>}
        <label>Password</label>
        <Controller
          render={({ field }) => <Input {...field} />}
          rules= {{
            required: "Wow, you still don't have a password?"
          }}
          name="password"
          control={control}
          defaultValue=""
          className="materialUIInput"
        />
        {<p>{errors.password?.message}</p>}
        <FormControl variant="standard" sx={{ m: 1, minWidth: 120 }}>
        <InputLabel id="demo-simple-select-standard-label">Choose dataset</InputLabel>
        <Select
          labelId="demo-simple-select-standard-label"
          name=""
          value={age}
          onChange={changeState}
          label="Choose dataset"
          required={"teest"}
        >

          <MenuItem value={"Romashka"}>Romashka</MenuItem>
          <MenuItem value={"VTB"}>VTB</MenuItem>

        </Select>
        {/* {<p>{errors.}</p>} */}
      </FormControl>
        <input type="submit" />
      </form>
    </div>
  );
};
