import React, {useEffect} from 'react';
import {NavBar} from "./NavBar";
import {Main} from "./Main";

const Layout = ({children}) => {
    useEffect(() => {
        if(localStorage.getItem('auth')) {
            setUser({username: localStorage.getItem('username' || '')})
            setIsAuth(true);
        }
    }, [])

    return (
        <>
            <NavBar/>
            <Main>
                {children}
            </Main>
        </>
    );
};

export default Layout;
